const state = {
  vibe: 'Playful',
  soundOn: true,
  civicOn: false,
  filters: { time: 'Any', energy: 'Any', where: 'Any', lane: 'Any' },
  selected: null,
  history: []
};

const el = (id) => document.getElementById(id);
const screens = {
  spin: el('screenSpin'),
  activity: el('screenActivity'),
  progress: el('screenProgress'),
  profile: el('screenProfile'),
  lanes: el('screenLanes')
};

function showScreen(name){ Object.values(screens).forEach(s=>s.classList.remove('active')); screens[name].classList.add('active'); }

function syncTopToggles(){ 
  el('soundToggle').querySelector('.switch').classList.toggle('off', !state.soundOn);
  el('civicToggle').querySelector('.switch').classList.toggle('off', !state.civicOn);
}
function syncChips(){
  el('chipTime').textContent = state.filters.time;
  el('chipEnergy').textContent = state.filters.energy;
  el('chipWhere').textContent = state.filters.where;
  el('chipLane').textContent = state.filters.lane;
  el('chipVibe').textContent = state.vibe;
}

function emojiForLane(lane){
  const x=(lane||'').toLowerCase();
  if(x.includes('creative'))return '📸';
  if(x.includes('meaning'))return '🌿';
  if(x.includes('humor'))return '😄';
  if(x.includes('problem'))return '🧩';
  if(x.includes('energy'))return '⚡';
  if(x.includes('weekend'))return '🌤️';
  return '🎯';
}

function renderMetaChips(container,a){
  const chips=[];
  if(a.timeMin!=null){
    const tm=a.timeMin;
    const t = tm<=5?'0–5 min':tm<=10?'5–10 min':tm<=20?'10–20 min':tm<=40?'20–40 min':'40+ min';
    chips.push(['⏱️',t]);
  }
  if(a.energy) chips.push(['⚡',a.energy]);
  if(a.where) chips.push(['📍',a.where]);
  if(a.lane) chips.push(['🧭',a.lane]);
  const tier = a.tier ?? 1;
  const scope = a.scope ? ` • Scope ${a.scope}` : '';
  chips.push(['🧱',`Tier ${tier}${scope}`]);
  if(state.civicOn || a.civic) chips.push(['🌱', a.civic ? 'Civic' : 'Mixed']);

  container.innerHTML='';
  chips.forEach(([i,t])=>{
    const d=document.createElement('div');
    d.className='mchip';
    d.innerHTML=`<span class="i">${i}</span><span>${t}</span>`;
    container.appendChild(d);
  });
}

function activityMatches(a){
  if(state.civicOn && !a.civic) return false;
  if(state.filters.lane!=='Any' && (a.lane||'').toLowerCase()!==state.filters.lane.toLowerCase()) return false;
  if(state.filters.energy!=='Any' && (a.energy||'').toLowerCase()!==state.filters.energy.toLowerCase()) return false;

  if(state.filters.time!=='Any' && a.timeMin!=null){
    const tm=a.timeMin, f=state.filters.time;
    if(f==='0–5' && !(tm<=5)) return false;
    if(f==='5–10' && !(tm>5 && tm<=10)) return false;
    if(f==='10–20' && !(tm>10 && tm<=20)) return false;
    if(f==='20–40' && !(tm>20 && tm<=40)) return false;
    if(f==='40+' && !(tm>40)) return false;
  }

  if(state.filters.where!=='Any'){
    const w=(a.where||'').toLowerCase();
    const f=state.filters.where.toLowerCase();
    if(!w.includes(f)) return false;
  }
  return true;
}

function getMatches(){ return ACTIVITIES.filter(activityMatches); }

function updateMatchesMeta(matches){
  const tier = state.selected?.tier ?? 1;
  const scope = state.selected?.scope ?? 1;
  el('matchesMeta').textContent = `Matches: ${matches.length} • Tier: ${tier} • Scope: ${scope}`;
}

function pickRandom(matches){ return matches.length ? matches[Math.floor(Math.random()*matches.length)] : null; }

function setSelected(a){
  state.selected=a;

  el('activityEmoji').textContent=emojiForLane(a.lane);
  el('activityTitle').textContent=a.title;
  el('activityDesc').textContent=a.text;
  renderMetaChips(el('activityMetaChips'),a);
  el('spinResultCard').hidden=false;

  el('activityEmoji2').textContent=emojiForLane(a.lane);
  el('activityTitle2').textContent=a.title;
  el('activityDesc2').textContent=a.text;
  renderMetaChips(el('activityMetaChips2'),a);

  updateMatchesMeta(getMatches());
}

function doSpin(){
  const wheel=el('spinWheel');
  const matches=getMatches();
  updateMatchesMeta(matches);

  wheel.classList.remove('spinning');
  void wheel.offsetWidth;
  wheel.classList.add('spinning');

  setTimeout(()=>{
    const a=pickRandom(matches);
    if(!a){
      el('spinResultCard').hidden=false;
      el('activityEmoji').textContent='🫥';
      el('activityTitle').textContent='No matches';
      el('activityDesc').textContent='Try clearing filters or turning Civic off.';
      el('activityMetaChips').innerHTML='';
      return;
    }
    setSelected(a);
  },650);
}

/* Progress timer */
let timerInterval=null;
function startTimer(seconds=120){
  let s=seconds;
  const timerEl=el('timer');
  const fill=el('progressFill');
  fill.style.width='0%';
  clearInterval(timerInterval);
  timerInterval=setInterval(()=>{
    s=Math.max(0,s-1);
    const mm=String(Math.floor(s/60)).padStart(2,'0');
    const ss=String(s%60).padStart(2,'0');
    timerEl.textContent=`${mm}:${ss}`;
    const pct=Math.round(((seconds-s)/seconds)*100);
    fill.style.width=`${pct}%`;
    if(s===0) clearInterval(timerInterval);
  },1000);
}

/* Profile history */
function addToHistory(a){ state.history.unshift({id:a.id,title:a.title,ts:new Date().toISOString()}); state.history=state.history.slice(0,30); }

function renderProfile(){
  const grid=el('dotGrid'); grid.innerHTML='';
  const done=state.history.length, cells=50;
  for(let i=0;i<cells;i++){ const d=document.createElement('div'); d.className='dotcell'+(i<done?' on':''); grid.appendChild(d);}

  const list=el('recentList'); list.innerHTML='';
  if(!state.history.length){
    const row=document.createElement('div'); row.className='rrow';
    row.innerHTML=`<div class="t">No completions yet</div><div class="s">Spin and try one</div>`;
    list.appendChild(row); return;
  }
  state.history.slice(0,8).forEach(h=>{
    const row=document.createElement('div'); row.className='rrow';
    const when=new Date(h.ts);
    row.innerHTML=`<div class="t">${h.title}</div><div class="s">${when.toLocaleString()}</div>`;
    list.appendChild(row);
  });
}

/* Lanes */
function renderLanes(){
  const lanes=Array.from(new Set(ACTIVITIES.map(a=>a.lane).filter(Boolean))).sort();
  const wrap=el('laneList'); wrap.innerHTML='';
  ['Any',...lanes].forEach(l=>{
    const b=document.createElement('button');
    b.className='lane-btn'+(state.filters.lane.toLowerCase()===l.toLowerCase()?' active':'');
    b.textContent=l;
    b.onclick=()=>{ state.filters.lane=(l==='Any')?'Any':l; syncChips(); updateMatchesMeta(getMatches()); renderLanes(); };
    wrap.appendChild(b);
  });
}

/* Modal */
let activeModal=null;
function openModal(kind){
  activeModal=kind;
  el('modalBackdrop').hidden=false;
  el('modal').hidden=false;
  el('modalBody').dataset.selected='';
  el('modalBody').innerHTML='';

  let options=[];
  let current=null;
  if(kind==='time'){ el('modalTitle').textContent='Time'; options=['0–5','5–10','10–20','20–40','40+']; current=state.filters.time; }
  if(kind==='energy'){ el('modalTitle').textContent='Energy'; options=['Low','Moderate','High']; current=state.filters.energy; }
  if(kind==='where'){ el('modalTitle').textContent='Where'; options=['Anywhere','Solo','Together']; current=state.filters.where; }
  if(kind==='lane'){ el('modalTitle').textContent='Lane'; options=Array.from(new Set(ACTIVITIES.map(a=>a.lane).filter(Boolean))).sort().slice(0,18); current=state.filters.lane; }
  if(kind==='adjust'){ el('modalTitle').textContent='Adjust choice'; options=['Keep this energy','Switch the vibe','Surprise me']; current='Keep this energy'; }

  const grid=document.createElement('div'); grid.className='option-grid';
  el('modalBody').appendChild(grid);

  options.forEach(opt=>{
    const b=document.createElement('button');
    b.className='opt'+(opt===current?' active':'');
    b.textContent=opt;
    b.onclick=()=>{
      grid.querySelectorAll('.opt').forEach(x=>x.classList.remove('active'));
      b.classList.add('active');
      el('modalBody').dataset.selected=opt;
    };
    grid.appendChild(b);
  });
}

function closeModal(){ activeModal=null; el('modalBackdrop').hidden=true; el('modal').hidden=true; }

function applyVibe(){
  const r=document.documentElement;
  if(state.vibe==='Calm'){ r.style.setProperty('--bg1','#8ff0ff'); r.style.setProperty('--bg2','#4c8cff'); r.style.setProperty('--bg3','#8a7bff'); }
  else if(state.vibe==='Bold'){ r.style.setProperty('--bg1','#7cffb0'); r.style.setProperty('--bg2','#3a79ff'); r.style.setProperty('--bg3','#ff4bd1'); }
  else { r.style.setProperty('--bg1','#7cf0c9'); r.style.setProperty('--bg2','#5b6cff'); r.style.setProperty('--bg3','#7b4bff'); }
}

function applyModal(){
  const selected=el('modalBody').dataset.selected;
  if(!activeModal) return closeModal();
  if(!selected) return closeModal();

  if(activeModal==='adjust'){
    if(selected==='Switch the vibe'){ const vibes=['Playful','Calm','Bold']; state.vibe=vibes[(vibes.indexOf(state.vibe)+1)%vibes.length]; }
    if(selected==='Surprise me'){ const vibes=['Playful','Calm','Bold']; state.vibe=vibes[Math.floor(Math.random()*vibes.length)]; }
    applyVibe();
  } else {
    if(activeModal==='time') state.filters.time=selected;
    if(activeModal==='energy') state.filters.energy=selected;
    if(activeModal==='where') state.filters.where=selected;
    if(activeModal==='lane') state.filters.lane=selected;
  }
  syncChips();
  updateMatchesMeta(getMatches());
  closeModal();
}

function clearModal(){
  if(activeModal==='time') state.filters.time='Any';
  if(activeModal==='energy') state.filters.energy='Any';
  if(activeModal==='where') state.filters.where='Any';
  if(activeModal==='lane') state.filters.lane='Any';
  syncChips();
  updateMatchesMeta(getMatches());
  closeModal();
}

/* Wire up */
function init(){
  syncTopToggles();
  syncChips();
  applyVibe();
  updateMatchesMeta(getMatches());

  el('spinWheel').addEventListener('click', doSpin);
  el('spinAgainBtn').addEventListener('click', doSpin);
  el('startBtn').addEventListener('click', ()=>{ if(!state.selected) return; showScreen('activity'); });

  el('activityBackToSpin').addEventListener('click', ()=>showScreen('spin'));
  el('activitySpinAgain').addEventListener('click', ()=>{ showScreen('spin'); doSpin(); });
  el('activityAdjust').addEventListener('click', ()=>openModal('adjust'));
  el('activityStart').addEventListener('click', ()=>{ if(!state.selected) return; el('progressName').textContent=state.selected.title; showScreen('progress'); startTimer(120); });

  el('progressSpinAgain').addEventListener('click', ()=>{ showScreen('spin'); doSpin(); });
  el('progressProfile').addEventListener('click', ()=>{ renderProfile(); showScreen('profile'); });
  el('markDone').addEventListener('click', ()=>{ if(!state.selected) return; addToHistory(state.selected); renderProfile(); showScreen('profile'); });

  el('profileBtn').addEventListener('click', ()=>{ renderProfile(); showScreen('profile'); });
  el('navProfile').addEventListener('click', ()=>{ renderProfile(); showScreen('profile'); });
  el('navProfile2').addEventListener('click', ()=>{ renderProfile(); showScreen('profile'); });
  el('navProfile3').addEventListener('click', ()=>{ renderProfile(); showScreen('profile'); });

  el('navLanes').addEventListener('click', ()=>{ renderLanes(); showScreen('lanes'); });
  el('navLanes2').addEventListener('click', ()=>{ renderLanes(); showScreen('lanes'); });
  el('navLanes3').addEventListener('click', ()=>{ renderLanes(); showScreen('lanes'); });

  el('profileBack').addEventListener('click', ()=>showScreen('spin'));
  el('lanesBack').addEventListener('click', ()=>showScreen('spin'));

  el('brandToSpin').addEventListener('click', ()=>showScreen('spin'));

  document.querySelectorAll('.chip').forEach(ch=>ch.addEventListener('click', ()=>openModal(ch.dataset.filter)));

  el('modalClose').addEventListener('click', closeModal);
  el('modalBackdrop').addEventListener('click', closeModal);
  el('modalApply').addEventListener('click', applyModal);
  el('modalClear').addEventListener('click', clearModal);

  el('soundToggle').addEventListener('click', ()=>{ state.soundOn=!state.soundOn; syncTopToggles(); });
  el('civicToggle').addEventListener('click', ()=>{ 
    state.civicOn=!state.civicOn; 
    syncTopToggles(); 
    updateMatchesMeta(getMatches());
    if(state.selected && state.civicOn && !state.selected.civic){ state.selected=null; el('spinResultCard').hidden=true; }
  });

  el('revealBtn').addEventListener('click', ()=>{
    if(!state.selected) return;
    const bonus = state.selected.weekend ? "Bonus: Great for weekend momentum." : "Bonus: Make one detail surprising.";
    el('activityDesc').textContent = state.selected.text + "\n\n" + bonus;
  });
}

init();
